file.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --blue: #7cb7ff;
  --red: #ff7c7c;
}

.game-canvas {
  -webkit-user-select: none;
  -webkit-touch-callout: none;
  user-select: none;
  touch-action: none;
}

.no-action {
  touch-action: none;
}

body {
  font-family: Arial, Helvetica, sans-serif;
  -webkit-user-select: none;
  -webkit-touch-callout: none;
}

.scale-animate {
  transition: transform 0.3s ease; /* Smooth animation for scaling */
}

.scale-animate:hover {
  transform: scale(1.2); /* Scale up the element on hover */
}

/* Red and Blue striped background with wider stripes */
.stripe-red-blue {
  background: repeating-linear-gradient(
    45deg,
    /* Diagonal stripes */ rgb(248, 208, 208),
    /* Start with red */ rgb(248, 208, 208) 20px,
    /* Wider red stripe width */ rgb(191, 219, 254) 20px,
    /* Start of blue */ rgb(191, 219, 254) 40px /* Wider blue stripe width */
  );
}

/* Red and Transparent striped background with wider stripes */
.stripe-red-transparent {
  background: repeating-linear-gradient(
    45deg,
    /* Diagonal stripes */ rgb(248, 208, 208),
    /* Start with red */ rgb(248, 208, 208) 20px,
    /* Wider red stripe width */ white 20px,
    /* Start of transparent */ white 40px /* Wider transparent stripe width */
  );
}

/* Blue and Transparent striped background with wider stripes */
.stripe-blue-transparent {
  background: repeating-linear-gradient(
    45deg,
    /* Diagonal stripes */ rgb(191, 219, 254),
    /* Start with blue */ rgb(191, 219, 254) 20px,
    /* Wider blue stripe width */ white 20px,
    /* Start of transparent */ white 40px /* Wider transparent stripe width */
  );
}

.stripe-blue-transparent-sm {
  background: repeating-linear-gradient(
    45deg,
    /* Diagonal stripes */ rgb(191, 219, 254),
    /* Start with blue */ rgb(191, 219, 254) 6px,
    /* Wider blue stripe width */ white 6px,
    /* Start of transparent */ white 12px /* Wider transparent stripe width */
  );
}

.target-square {
  cursor: pointer;
  width: 100%;
  height: 100%;
  position: absolute;
  pointer-events: none; /* Ensures the overlay doesn't interfere with clicks */
}
.target-square::after {
  content: "⌖"; /* The Unicode character */
  position: absolute;
  top: -45%;
  left: 50%;
  font-weight: bolder;
  transform: translate(-50%, -50%);
  font-size: 30px; /* Adjust the size of the ⌖ character */
  color: #da5f73; /* Color of the Unicode character */
  cursor: pointer;
  pointer-events: none; /* Ensures the overlay doesn't interfere with clicks */
}

.animate-move {
  transition: left 0.2s ease-out, top 0.2s ease-out, transform 0.2s ease-out;
}

.animate-fade-in {
  opacity: 0;
  animation: fadeIn 0.1s forwards;
}

@keyframes fadeIn {
  to {
    opacity: 1;
  }
}
@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;
    --primary: 222.2 47.4% 11.2%;
    --primary-foreground: 210 40% 98%;
    --secondary: 210 40% 96.1%;
    --secondary-foreground: 222.2 47.4% 11.2%;
    --muted: 210 40% 96.1%;
    --muted-foreground: 215.4 16.3% 46.9%;
    --accent: 210 40% 96.1%;
    --accent-foreground: 222.2 47.4% 11.2%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 222.2 84% 4.9%;
    --chart-1: 12 76% 61%;
    --chart-2: 173 58% 39%;
    --chart-3: 197 37% 24%;
    --chart-4: 43 74% 66%;
    --chart-5: 27 87% 67%;
    --radius: 0.5rem;
  }
  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --chart-1: 220 70% 50%;
    --chart-2: 160 60% 45%;
    --chart-3: 30 80% 55%;
    --chart-4: 280 65% 60%;
    --chart-5: 340 75% 55%;
  }
}
@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}

