     1→import React, { ReactNode } from "react";
     2→import {
     3→  AllowedMove,
     4→  Coordinate,
     5→  GameoverState,
     6→  GHQState,
     7→  HistoryItem,
     8→  Orientation,
     9→  Player,
    10→  Square,
    11→  Units,
    12→  UnitType,
    13→} from "@/game/engine";
    14→import { BoardProps } from "boardgame.io/react";
    15→import { coordinateToAlgebraic, degreesToCardinal } from "./notation";
    16→import { LogEntry } from "boardgame.io";
    17→import {
    18→  ArrowBigRightDash,
    19→  Bomb,
    20→  Crosshair,
    21→  Flag,
    22→  RotateCw,
    23→  Ship,
    24→  SkipForward,
    25→  User,
    26→} from "lucide-react";
    27→import { allowedMoveFromUci } from "./notation-uci";
    28→
    29→export function HistoryLog({
    30→  systemMessages,
    31→  log,
    32→  gameover,
    33→}: {
    34→  systemMessages?: HistoryItem[];
    35→  log: BoardProps<GHQState>["log"];
    36→  gameover?: GameoverState;
    37→}) {
    38→  const filteredLog: LogEntry[] = [];
    39→
    40→  const undoneMoves: LogEntry[] = [];
    41→  for (const entry of log) {
    42→    if (entry.action.type === "UNDO") {
    43→      const lastMove = filteredLog.pop();
    44→      lastMove && undoneMoves.push(lastMove);
    45→    } else if (entry.action.type === "REDO") {
    46→      const lastUndoneMove = undoneMoves.pop();
    47→      lastUndoneMove && filteredLog.push(lastUndoneMove);
    48→    } else {
    49→      filteredLog.push(entry);
    50→    }
    51→  }
    52→
    53→  const playerMessages = filteredLog
    54→    .filter((entry) => entry.action.type === "MAKE_MOVE")
    55→    .map((entry) => {
    56→      let { playerID, type, args } = entry.action.payload;
    57→      const player = playerID === "0" ? "RED" : "BLUE";
    58→      let description = type;
    59→      let reactNode: ReactNode | null = null;
    60→      const pieceType = entry?.metadata?.pieceType?.toLowerCase() ?? "piece";
    61→
    62→      // Shim for UCI moves
    63→      if (entry.metadata?.uci) {
    64→        const move = allowedMoveFromUci(entry.metadata?.uci);
    65→        if (move) {
    66→          type = move.name;
    67→          args = move.args;
    68→        }
    69→      }
    70→
    71→      if (type === "Move") {
    72→        const [from, to] = args;
    73→        const fromNotation = coordinateToAlgebraic(from);
    74→        const toNotation = coordinateToAlgebraic(to);
    75→        description = `moved ${pieceType} from ${fromNotation} to ${toNotation}`;
    76→
    77→        const capture = entry?.metadata?.capturePreference;
    78→        if (capture) {
    79→          const captureType = entry?.metadata?.capturedPiece?.type ?? "piece";
    80→          const captureNotation = coordinateToAlgebraic(capture);
    81→          description += ` and captured ${captureType.toLowerCase()} on ${captureNotation}`;
    82→        }
    83→
    84→        reactNode = movedPiece({
    85→          player,
    86→          unitType: entry?.metadata?.pieceType,
    87→          from,
    88→          to,
    89→          capturedPiece: entry?.metadata?.capturedPiece,
    90→          capturedCoordinate: entry?.metadata?.capturePreference,
    91→          description,
    92→        });
    93→      } else if (type === "Reinforce") {
    94→        const [kind, at] = args;
    95→        const atNotation = coordinateToAlgebraic(at);
    96→        description = `reinforced with ${kind.toLowerCase()} at ${atNotation}`;
    97→        reactNode = reinforcedPiece({
    98→          player,
    99→          unitType: kind,
   100→          at,
   101→          description,
   102→        });
   103→      } else if (type === "MoveAndOrient") {
   104→        const [from, to, orientation] = args;
   105→        const fromNotation = coordinateToAlgebraic(from);
   106→        const toNotation = coordinateToAlgebraic(to);
   107→        description = `moved ${pieceType} from ${fromNotation} to ${toNotation} and rotated ${degreesToCardinal(
   108→          orientation
   109→        )}`;
   110→        reactNode = movedPiece({
   111→          player,
   112→          unitType: entry?.metadata?.pieceType,
   113→          from,
   114→          to,
   115→          orientation,
   116→          description,
   117→        });
   118→      } else if (type === "ChangeOrientation") {
   119→        const [at, orientation] = args;
   120→        const atNotation = coordinateToAlgebraic(at);
   121→        description = `rotated ${pieceType} at ${atNotation} ${degreesToCardinal(
   122→          orientation
   123→        )}`;
   124→      } else if (type === "Skip") {
   125→        description = "skipped rest of turn";
   126→        reactNode = playerSkip({ player, description });
   127→      } else if (type === "Resign") {
   128→        description = "resigned";
   129→        reactNode = playerResign({ player, description });
   130→      } else if (type === "AutoCapture") {
   131→        return null;
   132→      } else {
   133→        reactNode = playerAction({
   134→          player,
   135→          action: type,
   136→          description: `${player} ${type}`,
   137→        });
   138→      }
   139→      return {
   140→        turn: entry.turn,
   141→        message: `${player} ${description}`,
   142→        reactNode,
   143→      };
   144→    })
   145→    .filter((m) => m !== null);
   146→
   147→  const systemCaptureMessages =
   148→    systemMessages?.flatMap(
   149→      ({ turn, playerId, captured, message, capturedByInfantry }) => {
   150→        if (message) {
   151→          return { turn, message, reactNode: null };
   152→        }
   153→
   154→        if (captured) {
   155→          const player = playerId === "0" ? "RED" : "BLUE";
   156→          const clearedSquares = captured.map(({ coordinate }) => coordinate);
   157→
   158→          if (capturedByInfantry) {
   159→            return capturedByInfantry.map((infantry, i) => {
   160→              const attackerPieceType =
   161→                typeof infantry.piece.type === "string"
   162→                  ? infantry.piece.type.toLowerCase()
   163→                  : "piece";
   164→              const capturedPieceType =
   165→                typeof captured[i]?.square?.type === "string"
   166→                  ? captured[i]?.square?.type?.toLowerCase()
   167→                  : "piece";
   168→              return {
   169→                turn,
   170→                message: `${player} ${attackerPieceType} captured ${capturedPieceType} at ${coordinateToAlgebraic(
   171→                  clearedSquares[i]
   172→                )} at start of turn`,
   173→                reactNode: startOfTurnCapture({
   174→                  player,
   175→                  capturedPiece: captured[i].square,
   176→                  capturedCoordinate: clearedSquares[i],
   177→                  description: `${player} ${attackerPieceType} captured ${capturedPieceType} at ${coordinateToAlgebraic(
   178→                    clearedSquares[i]
   179→                  )} at start of turn`,
   180→                }),
   181→              };
   182→            });
   183→          } else {
   184→            return clearedSquares.map((coord) => {
   185→              return {
   186→                turn,
   187→                message: `${player} artillery bombarded piece at ${coordinateToAlgebraic(
   188→                  coord
   189→                )} at start of turn`,
   190→                reactNode: startOfTurnCapture({
   191→                  player,
   192→                  capturedCoordinate: coord,
   193→                  isBombardment: true,
   194→                  description: `${player} artillery bombarded piece at ${coordinateToAlgebraic(
   195→                    coord
   196→                  )} at start of turn`,
   197→                }),
   198→              };
   199→            });
   200→          }
   201→        }
   202→
   203→        return { turn, message: "", reactNode: null };
   204→      }
   205→    ) ?? [];
   206→
   207→  const combinedMessages = [...systemCaptureMessages, ...playerMessages].sort(
   208→    (a, b) => a.turn - b.turn
   209→  );
   210→
   211→  // TODO(tyler): figure out why duplicate messages are coming through
   212→  const deduplicatedMessages = combinedMessages.filter(
   213→    (msg, index, self) =>
   214→      index === self.findIndex((m) => m.message === msg.message)
   215→  );
   216→
   217→  React.useEffect(() => {
   218→    const messagesDiv = document.querySelector("#history-log-list");
   219→    if (messagesDiv) {
   220→      messagesDiv.scrollTop = messagesDiv.scrollHeight;
   221→    }
   222→  }, [playerMessages]);
   223→
   224→  function handleClick(turn: number) {
   225→    const event = new CustomEvent("historyLogTurnClick", { detail: { turn } });
   226→    window.dispatchEvent(event);
   227→  }
   228→
   229→  return (
   230→    <div className="flex flex-col gap-1 p-2 h-[350px]">
   231→      <div className="font-bold text-lg">Activity</div>
   232→      <div
   233→        id="history-log-list"
   234→        className="overflow-y-auto border h-[600px] flex flex-col rounded"
   235→      >
   236→        {deduplicatedMessages.map((msg) => (
   237→          <div
   238→            key={msg.message}
   239→            className="inline-flex space-x-2 items-center hover:bg-gray-100 py-0.5 px-1"
   240→            onClick={() => handleClick(msg.turn)}
   241→          >
   242→            <span className="text-gray-600 text-xs">{msg.turn} </span>
   243→            {msg.reactNode ? msg.reactNode : <div>{msg.message}</div>}
   244→          </div>
   245→        ))}
   246→
   247→        {gameover && (
   248→          <div className="inline-flex space-x-2 items-center">
   249→            {gameover.status === "DRAW" && "Game ended in a draw"}
   250→            {gameover.winner && (
   251→              <>
   252→                {toTitleCase(gameover.winner)} won {gameover.reason}!
   253→              </>
   254→            )}
   255→          </div>
   256→        )}
   257→      </div>
   258→    </div>
   259→  );
   260→}
   261→
   262→function toTitleCase(str: string) {
   263→  return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
   264→}
   265→
   266→function movedPiece({
   267→  player,
   268→  unitType,
   269→  from,
   270→  to,
   271→  capturedPiece,
   272→  capturedCoordinate,
   273→  orientation,
   274→  description,
   275→}: {
   276→  player: Player;
   277→  unitType: UnitType;
   278→  from: Coordinate;
   279→  to: Coordinate;
   280→  capturedPiece?: Square;
   281→  capturedCoordinate?: Coordinate;
   282→  orientation?: Orientation;
   283→  description: string;
   284→}): ReactNode {
   285→  return (
   286→    <div
   287→      className="inline-flex items-center space-x-2 text-sm"
   288→      title={description}
   289→    >
   290→      <PieceIcon player={player} unitType={unitType} />
   291→      <div className="align-text-bottom">{coordinateToAlgebraic(from)}</div>
   292→      <ArrowBigRightDash className="w-3 h-3 inline-block" />
   293→      <div className="align-text-bottom">{coordinateToAlgebraic(to)}</div>
   294→      {capturedPiece && capturedCoordinate && (
   295→        <div className="inline-flex items-center space-x-2">
   296→          <Crosshair className="w-3 h-3" />
   297→          <PieceIcon
   298→            player={capturedPiece.player}
   299→            unitType={capturedPiece.type}
   300→          />
   301→          <div className="align-text-bottom">
   302→            {coordinateToAlgebraic(capturedCoordinate)}
   303→          </div>
   304→        </div>
   305→      )}
   306→      {orientation !== undefined && (
   307→        <div className="inline-flex items-center space-x-2">
   308→          <RotateCw className="w-3 h-3" />
   309→          <div className="align-text-bottom">
   310→            {degreesToCardinal(orientation)}
   311→          </div>
   312→        </div>
   313→      )}
   314→    </div>
   315→  );
   316→}
   317→
   318→function PieceIcon({
   319→  player,
   320→  unitType,
   321→}: {
   322→  player: Player;
   323→  unitType: UnitType;
   324→}) {
   325→  return (
   326→    <img
   327→      className="inline-block"
   328→      src={`/${Units[unitType].imagePathPrefix}-${player.toLowerCase()}.png`}
   329→      width={12}
   330→      height={12}
   331→      draggable="false"
   332→      alt={Units[unitType].imagePathPrefix}
   333→    />
   334→  );
   335→}
   336→
   337→function reinforcedPiece({
   338→  player,
   339→  unitType,
   340→  at,
   341→  description,
   342→}: {
   343→  player: Player;
   344→  unitType: UnitType;
   345→  at: Coordinate;
   346→  description: string;
   347→}): ReactNode {
   348→  return (
   349→    <div
   350→      className="inline-flex items-center space-x-2 text-sm"
   351→      title={description}
   352→    >
   353→      <PieceIcon player={player} unitType={unitType} />
   354→      <Ship className="w-3 h-3 inline-block" />
   355→      <div className="align-text-bottom">{coordinateToAlgebraic(at)}</div>
   356→    </div>
   357→  );
   358→}
   359→
   360→function playerSkip({
   361→  player,
   362→  description,
   363→}: {
   364→  player: Player;
   365→  description: string;
   366→}): ReactNode {
   367→  return (
   368→    <div
   369→      className="inline-flex items-center space-x-2 text-sm"
   370→      title={description}
   371→    >
   372→      <PlayerIcon player={player} />
   373→      <SkipForward className="w-3 h-3 inline-block" />
   374→    </div>
   375→  );
   376→}
   377→
   378→function PlayerIcon({ player }: { player: Player }): ReactNode {
   379→  return (
   380→    <User
   381→      className="w-4 h-4 inline-block"
